mapper.select:

			SELECT *
  		      FROM(
        		   SELECT A.*,ROWNUM AS RNUM
         			 FROM(
                		  SELECT *
                  			FROM BOARD
                 		   WHERE CATEGORY=#{category}
                		   ORDER BY BNO DESC
              			 ) A
         	   	   WHERE ROWNUM<=#{rowEnd}
      			  )
 			 WHERE RNUM>=#{rowStart}

mapper.update:
UPDATE BOARD
   SET SUBJECT=#{subject}
     , CATEGORY=#{category}
     , CONTENT=#{content}
 WHERE BNO=#{bno}

mapper.delete:
DELETE 
  FROM BOARD
 WHERE CATEGORY=#{category} AND BNO=#{bno}

mapper.update:
UPDATE BOARD
   SET READCOUNT = READCOUNT+1
 WHERE BNO=#{bno}

mapper.select:
SELECT COUNT(*)
  FROM BOARD
 WHERE CATEGORY=#{category}
