mapper.select:

SELECT ROW_NUMBER() OVER(ORDER BY a.CARTNO ASC) AS NUM, a.CARTNO, a.MNO, a.PNO, a.ONO, b.BRAND, b.PNAME, b.PRODUCT_THUMURL, b.PRICE, b.SHIPPING_FEE
  FROM TBL_CART a INNER JOIN PRODUCT b ON a.PNO = b.PNO
 WHERE a.MNO = #{mno}

mapper.update:
UPDATE TBL_CART
   SET INVENTORY=#{inventory}
 WHERE CARTNO=#{cartno}
   AND PNO=#{pno}
   AND MNO=#{mno}

mapper.delete:
DELETE
  FROM TBL_CART
 WHERE PNO = #{pno}
   AND MNO = #{mno}

DELETE
  FROM TBL_CART
 WHERE CARTNO = #{cartno}
   AND MNO = #{mno}

mapper.select:
SELECT CARTNO
  FROM TBL_CART
 WHERE ONO = #{ono}
   AND MNO = #{mno}

mapper.update:
UPDATE TBL_CART
   SET INVENTORY=INVENTORY+#{inventory}
 WHERE CARTNO = #{cartno}
