select.if:

			SELECT *
  		      FROM(
        		   SELECT A.*,ROWNUM AS RNUM
         			 FROM(
                		  SELECT *
                  			FROM BOARD
                  		   WHERE CATEGORY = '공지사항'
                		   ORDER BY BNO DESC
              			 ) A
         	   	   WHERE ROWNUM <= #{rowEnd}
      			  )
 			 WHERE RNUM >= #{rowStart}
