mapper.update:
UPDATE TBL_PROMOTIONS
   SET STATUS = #{status}
 WHERE PNO = #{pno}

UPDATE TBL_PROMOTIONS SET SUBJECT = #{subject},
THUMBNAIL_URL = #{thumbnailurl},
IMAGES_URL = #{imagesurl}, STARTDATE = #{startdate}, ENDDATE = #{enddate}, STATUS = #{status}

mapper.select:

SELECT *
  FROM(
       SELECT ROWNUM AS RNUM, A.*
         FROM(    
              SELECT B.RNO,B.PNO,A.MNO,A.NAME,B.CONTENT,TO_CHAR(B.REPLY_TIME,'YYYY-MM-DD HH24:MI:SS') AS REPLY_TIME
                FROM MEMBER A INNER JOIN TBL_PROMOTIONS_COMMENT B ON A.MNO = B.MNO
               WHERE B.PNO = #{pno}
               ORDER BY B.REPLY_TIME DESC
         ) A
        WHERE ROWNUM <= #{rowEnd}
      )
 WHERE RNUM >= #{rowStart}

SELECT COUNT(*)
  FROM TBL_PROMOTIONS_COMMENT
 WHERE PNO = #{pno}
